def on_up_pressed():
    global walljumpOnCooldown
    if (Character.tile_kind_at(TileDirection.BOTTOM, assets.tile("""
        myTile0
    """)) or (Character.tile_kind_at(TileDirection.LEFT, assets.tile("""
        myTile0
    """)) or Character.tile_kind_at(TileDirection.RIGHT, assets.tile("""
        myTile0
    """))) and not (walljumpOnCooldown)) and not (ragdoll):
        Character.vy = -200
        walljumpOnCooldown = True
        
        def on_after():
            global walljumpOnCooldown
            walljumpOnCooldown = False
        timer.after(500, on_after)
        
controller.up.on_event(ControllerButtonEvent.PRESSED, on_up_pressed)

def on_countdown_end():
    global ragdoll
    ragdoll = False
info.on_countdown_end(on_countdown_end)

