def on_forever():
    global ragdoll
    Character.vy += 8
    if Character.tile_kind_at(TileDirection.CENTER, assets.tile("""
        myTile
    """)):
        ragdoll = True
        Character.vx = 100
        Character.vy = 0
        info.start_countdown(3)
    elif Character.tile_kind_at(TileDirection.CENTER, assets.tile("""
        myTile1
    """)):
        ragdoll = True
        Character.vx = -100
        Character.vy = 0
        info.start_countdown(3)
    elif Character.tile_kind_at(TileDirection.BOTTOM, assets.tile("""
        myTile
    """)):
        ragdoll = True
        Character.vx = 50
        info.start_countdown(3)
    elif Character.tile_kind_at(TileDirection.BOTTOM, assets.tile("""
        myTile1
    """)):
        ragdoll = True
        Character.vx = -50
        info.start_countdown(3)
    if Character.tile_kind_at(TileDirection.CENTER, assets.tile("""
        myTile3
    """)):
        info.stop_countdown()
        ragdoll = False
    if ragdoll == False:
        controller.move_sprite(Character, 100, 0)
    else:
        controller.move_sprite(Character, 0, 0)
forever(on_forever)
